var Util = {
    previewImg: function () {
        $("body").on("click", ".vditor-reset img", function () {
            $(this).hasClass("prevent") || window.open(this.src)
        })
    },  goTop: function () {
        $("html, body").animate({scrollTop: 0}, 800)
    }, goBottom: function (e) {
        e || (e = 0), $("html, body").animate({scrollTop: $(document).height() - $(window).height() - e}, 800)
    }, init: function () {
        Util.previewImg()
    },
};
var Skin = {
    _initCommon: function (e) {
        $(window).scroll(function () {
            125 < $(window).scrollTop() ? e.show() : e.hide(), 0 < $(".side .main").length && 768 < $(window).width() && (50 < $(window).scrollTop() ? $(".side").css("position", "fixed") : $(".side").css("position", "initial"))
        })
    }, init: function () {
        Util.init()
            , this._initCommon($(".icon__up")), $(".header__nav a, .header__m a").each(function () {
            this.href === location.href && (this.className = "current")
        }).click(function () {
            $(".header__nav a, .header__m a").removeClass("current"), this.className = "current", $(".header__m .module__list").hide()
        }), $(".header__logo").click(function () {
            $(".header__nav a, .header__m a").removeClass("current")
        })
    }
};
Skin.init();
